INSERT INTO db_blog.recipes(description, ingredient, photo_url, portion, prep_time_in_minutes, title)VALUES('Versare la pasta in acqua bollente, dopo il tempo di cottura necessario scolare. Nel frattempo soffriggere la cipolla, poi aggiungere la passata e far cuocere 10/15 minuti con olio e sale a piacere', '150g di pasta, 500g passata di pomodoro, 1 cipolla, olio q.b., sale q.b.', 'https://www.giallozafferano.it/images/221-22163/Spaghetti-al-pomodoro_650x433_wm.jpg', 2, 20, 'Pasta con sugo di pomodoro');